    //not empty, size >= 1
    @Size(min = 1, max = 240, message = "Enter a password")
    private String password;
    //Not empty no numbers
    @Size(min = 1, max = 240, message = "Enter a First Name")
    private String firstName;
    //Not empty no numbers
    @Size(min = 1, max = 240, message = "Enter a Last Name")
    private String lastName;
